import './App.css';
import Login from './pages/Login/Login';
import useFetch from './useFetch';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Quizzes from './quizzes/Quizzes';
import Questions from './questions/Questions';
import axios from 'axios';
import { useState, useEffect } from 'react';

function App() {
  const [userId, setUserId] = useState(null);
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    // Get user ID from backend API
    axios.get('/api/user')
      .then(response => {
        setUserId(response.data.id);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const { data, error } = useFetch("http://127.0.0.1:5000/participant/quizzes")

  useEffect(() => {
    if (data) {
      setQuizzes(data);
    }
  }, [data]);

  const handleUpdateQuiz = async (updatedQuiz) => {
    try {
      // Send a PATCH request to the backend API to update the quiz
      const response = await axios.patch(`http://127.0.0.1:5000/participant/quizzes/${updatedQuiz.id}`, updatedQuiz);

      // Update the quizzes state variable with the updated quiz
      const updatedQuizzes = quizzes.map((quiz) => {
        if (quiz.id === updatedQuiz.id) {
          return updatedQuiz;
        }
        return quiz;
      });

      // Update the quizzes state variable
      setQuizzes(updatedQuizzes);

      // Log a success message to the console
      console.log(`Quiz updated successfully: ${response.data}`);
    } catch (error) {
      // Log an error message to the console
      console.error(`Error updating quiz: ${error}`);
    }
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/' element={<Login />} />
          <Route path='/quizzes' element={<Quizzes quizzes={quizzes} userId={userId} handleUpdateQuiz={handleUpdateQuiz} />} />
          <Route path='quizzes/questions/:quizId' element={<Questions quizzes={quizzes} userId={userId} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
