import './App.css';
import Login from './pages/Login/Login';
import useFetch from './useFetch';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import Quizzes from './quizzes/Quizzes';
import Questions from './questions/Questions';
import axios from 'axios';
import { useState, useEffect } from 'react';
import QuizForm from './quizzes/QuizForm';
import QuizPage from './quizzes/QuizPage';
import SingleQuestion from './questions/SingleQuestion';
import SingleQuiz from './quizzes/SingleQuiz';
import AddQuestions from './questions/AddQuestions';

function App() {
  const [userId, setUserId] = useState(null);
  const [refresh, setRefresh] = useState(false);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const navigate = useNavigate();

  useEffect(() => {
    // Get user ID from backend API
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      axios.get('/api/user')
       .then(response => {
          setUserId(response.data.id);
        })
       .catch(error => {
          console.error(error);
        });
    }
  }, [token]);

  const { data, errors } = useFetch("http://127.0.0.1:5000/participant/quizzes", null, refresh)

  console.log(data, errors)

  const handleRefresh = () => {
    setRefresh(!refresh);
  }

  const handleLogout = () => {
    axios.post('/api/logout')
     .then(response => {
        localStorage.removeItem('token');
        setToken(null);
        setUserId(null);
        navigate('/login', { replace: true });
      })
     .catch(error => {
        console.error(error);
      });
  }

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/' element={<Login />} />
          <Route path='/quizzes' element={<Quizzes quizzes={data} userId={userId} logout={handleLogout} />} />
          <Route path='quizzes/questions/:quizId' element={<Questions quizzes={data} userId={userId} />} />
          <Route path= 'quizzes/create/quizzes' element = {<QuizPage quizzes = {data} onRefresh={handleRefresh} />} />
          <Route path="/add-questions/:quizId" element={<AddQuestions/>} />
        </Routes>
        {userId && (
          <nav>
            <Link to="/quizzes">Quizzes</Link>
            <button onClick={handleLogout}>Logout</button>
          </nav>
        )}
      </Router>
    </div>
  );
}

export default App;
