import React, { useState } from 'react';

function QuizForm({ onAddQuiz, onRefresh }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [text, setText] = useState('');
  const [choice_1, setChoice_1] = useState('');
  const [choice_2, setChoice_2] = useState('');
  const [choice_3, setChoice_3] = useState('');
  const [choice_4, setChoice_4] = useState('');
  const [answer, setAnswer] = useState('');
  const [quizId, setQuizId] = useState(null);

  function handleSubmit(e) {
    e.preventDefault();
    fetch("http://127.0.0.1:5000/creator/quizzes", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer ' + localStorage.getItem('token')
      },
      body: JSON.stringify({
        title: title,
        description: description,
      }),
    })
      .then((r) => r.json())
      .then((newQuiz) => {
        onAddQuiz(newQuiz);
        setQuizId(newQuiz.id); // Set the quizId state
        onRefresh(); // Call onRefresh after adding the new quiz
      });
  }

  function handleSubmitQuestion(e) {
    e.preventDefault();
    fetch("http://127.0.0.1:5000/creator/questions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer ' + localStorage.getItem('token')
      },
      body: JSON.stringify({
        text: text,
        choice_1: choice_1,
        choice_2: choice_2,
        choice_3: choice_3,
        choice_4: choice_4,
        answer: answer,
        quiz_id: quizId // Pass the quizId state
      }),
    })
      .then((r) => r.json())
      .then((newQuestion) => {
        console.log(newQuestion)
        onRefresh(); // Call onRefresh after adding the new question
      });
  }

  return (
    <div className="new-quiz-form">
      <h2>New Quiz</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Quiz title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="text"
          name="description"
          placeholder="Quiz description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">Add Quiz</button>
      </form>
        <form onSubmit={handleSubmitQuestion}>
          <input
            type="text"
            name="text"
            placeholder="Question text"
            value={text}
            onChange={(e) => setText(e.target.value)}
          />
          <input
            type="text"
            name="choice_1"
            placeholder="Choice 1"
            value={choice_1}
            onChange={(e) => setChoice_1(e.target.value)}
          />
          <input
            type="text"
            name="choice_2"
            placeholder="Choice 2"
            value={choice_2}
            onChange={(e) => setChoice_2(e.target.value)}
          />
          <input
            type="text"
            name="choice_3"
            placeholder="Choice 3"
            value={choice_3}
            onChange={(e) => setChoice_3(e.target.value)}
          />
          <input
            type="text"
            name="choice_4"
            placeholder="Choice 4"
            value={choice_4}
            onChange={(e) => setChoice_4(e.target.value)}
          />
          <input
            type="text"
            name="answer"
            placeholder="Answer"
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
          />
          <button type="submit">Add Question</button>
        </form>
    </div>
  );
}

export default QuizForm;
