import React, { useState } from 'react';
import Response from './Response';

const ResponseForm = ({ question_id, quiz_id, user_id }) => {
  const [response, setResponse] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);

  const [data, setData] = useState({});

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Response submitted:', response);

    // Make a POST request to create a new response
    fetch('http://localhost:5000/participant/responses', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        "Authorization": "Bearer " + localStorage.getItem('token')
      },
      body: JSON.stringify({ 
        response: response, 
        question_id: question_id, 
        quiz_id: quiz_id, 
        user_id: user_id 
      })
    })
      .then(response => response.json())
      .then(data => {
        console.log('Response created:', data);
        setIsSubmitted(true);
        setData(data);
        let result = data
        result.map(r => setIsCorrect(r.is_correct))
        console.log(isCorrect)
        useEffect(() => {

          if (isCorrect) {

            alert('Correct answer!');

          } else {

            alert('Incorrect answer. Try again!');

          }

        }, [isCorrect]);
      })
      .catch(error => console.error('Error creating response:', error));
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>Enter your response:</label>
        <input
          type="text"
          value={response}
          onChange={(event) => setResponse(event.target.value)}
          placeholder="Type your response here"
        />
        <button type="submit">Submit</button>
      </form>
      {isSubmitted && <Response response={response} data={data} />}
    </div>
  );
};

export default ResponseForm;
