import React from 'react';

function QuizForm({ onAddQuiz, quiz, setQuiz }) {
  const handleSubmit = (event) => {
    event.preventDefault();
    onAddQuiz(quiz);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setQuiz((prevQuiz) => ({ ...prevQuiz, [name]: value }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Title:
        <input type="text" name="title" value={quiz.title} onChange={handleInputChange} />
      </label>
      <br />
      <label>
        Description:
        <input type="text" name="description" value={quiz.description} onChange={handleInputChange} />
      </label>
      <br />
      <button type="submit">Add Quiz</button>
    </form>
  );
}

export default QuizForm;
