import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './Quizzes.css';
import { Link } from 'react-router-dom';
import useFetch from '../useFetch';
import Questions from '../questions/Questions';

function SingleQuiz({ quiz }) {
  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    // Fetch questions for the quiz from Flask backend
    fetch(`/api/quiz/${quiz.id}/questions`)
      .then(response => response.json())
      .then(data => {
        setQuestions(data.questions);
      })
      .catch(error => console.error('Error fetching questions:', error));
  }, [quiz.id]);

  return (
    <div className="card">
      <Link to={`/questions/${quiz.id}`}>
        <div className="card-body">
          <img src="qwerty" alt="Quiz" className="card-img-top" />
          <h5 className="card-title">{quiz.title}</h5>
          <h6 className="card-subtitle mb-2 text-body-secondary">{quiz.description}</h6>
          <p className="card-text">Created At: {quiz.created_at}</p>
          <p className="card-text">Updated At: {quiz.updated_at}</p>
          <Questions questions={questions} />
        </div>
      </Link>
    </div>
  );
}

export default SingleQuiz
