import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

function Questions() {
  const params = useParams(); // Get the URL parameters object
  const quizId = params.quizId; // Access the quizId property

  const [questions, setQuestions] = useState([]);

  useEffect(() => {
    console.log('quizId:', quizId);  // Debugging line
    if (quizId) {
      fetch(`http://127.0.0.1:5000/participant/quizzes/${quizId}/questions`, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          "Authorization": "Bearer " + localStorage.getItem('token')
        }
      })
        .then(response => {
          if (!response.ok) {
            console.error('Network response was not ok', response.status, response.statusText);
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then(data => {
          console.log('Fetched questions:', data);
          setQuestions(data);
        })
        .catch(error => console.error('Error fetching questions:', error));
    }
  }, [quizId]);

  return (
    <div>
      <h2>Questions for Quiz {quizId}</h2>
      <ul>
        {questions.map((question, index) => (
          <li key={index}>{question.text}</li>
        ))}
      </ul>
    </div>
  );
}

export default Questions;
