import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

function QuizForm({ onAddQuiz, onRefresh }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [loadingQuiz, setLoadingQuiz] = useState(false);
  const [error, setError] = useState('');
  const history = useHistory();

  function handleSubmit(e) {
    e.preventDefault();
    setLoadingQuiz(true);
    setError('');
    fetch("http://127.0.0.1:5000/creator/quizzes", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer ' + localStorage.getItem('token')
      },
      body: JSON.stringify({
        title: title,
        description: description,
      }),
    })
      .then((r) => r.json())
      .then((newQuiz) => {
        if (newQuiz.id) {
          onAddQuiz(newQuiz);
          setTitle('');
          setDescription('');
          onRefresh();
          history.push(`/add-questions/${newQuiz.id}`); // Redirect to AddQuestions page with quizId
        } else {
          throw new Error('Failed to create quiz');
        }
        setLoadingQuiz(false);
      })
      .catch((error) => {
        console.error("Error creating quiz:", error);
        setError("Failed to create quiz. Please try again.");
        setLoadingQuiz(false);
      });
  }

  return (
    <div className="new-quiz-form">
      <h2>New Quiz</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          placeholder="Quiz title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          disabled={loadingQuiz}
        />
        <input
          type="text"
          name="description"
          placeholder="Quiz description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          disabled={loadingQuiz}
        />
        <button type="submit" disabled={loadingQuiz}>
          {loadingQuiz ? 'Adding...' : 'Add Quiz'}
        </button>
      </form>
    </div>
  );
}

export default QuizForm;
