import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './Quizzes.css';
// import { Link } from 'react-router-dom';
// import useFetch from '../useFetch';
import Questions from '../questions/Questions';
import useFetch from '../useFetch';

  function SingleQuiz({ quiz }) {
    const [questions, setQuestions] = React.useState(null);

    const { data, error, isLoading } = useFetch(`http://127.0.0.1:5000/participant/quizzes/${quiz.id}/questions`);


  const handleQuizClick = async () => {

    if (!isLoading && !error) {

      setQuestions(data);

    }

  };
    return (
      <div className="card">
        <div className="card-body">
          <img src="qwerty" alt="Quiz" className="card-img-top" />
          <h5 className="card-title">{quiz.title}</h5>
          <h6 className="card-subtitle mb-2 text-body-secondary">{quiz.description}</h6>
          <p className="card-text">Created At: {quiz.created_at}</p>
          <p className="card-text">Updated At: {quiz.updated_at}</p>
          <button onClick={handleQuizClick}>Show Questions</button>
          {questions && <Questions quiz = {quiz}/>}
        </div>
      </div>
    );
  }


export default SingleQuiz;
