import './App.css';
import Login from './pages/Login/Login';
import useFetch from './useFetch';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Quizzes from './quizzes/Quizzes';
import Questions from './questions/Questions';
import axios from 'axios';
import { useState, useEffect } from 'react';
import QuizForm from './quizzes/QuizForm';
import QuizPage from './quizzes/QuizPage';

function App() {
  const [userId, setUserId] = useState(null);
  const [quizzes, setQuizzes] = useState([]); // add a state for quizzes

  useEffect(() => {
    // Get user ID from backend API
    axios.get('/api/user')
      .then(response => {
        setUserId(response.data.id);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    // Fetch quizzes data from API
    axios.get('http://127.0.0.1:5000/participant/quizzes')
      .then(response => {
        setQuizzes(response.data); // update the quizzes state
      })
      .catch(error => {
        console.error(error);
      });
  }, [userId]); // add userId as a dependency

  const handleAddQuiz = (newQuiz) => {
    // Add the new quiz to the quizzes state
    setQuizzes([...quizzes, newQuiz]);
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/' element={<Login />} />
          <Route path='/quizzes' element={<Quizzes quizzes={quizzes} userId={userId} />} />
          <Route path='quizzes/questions/:quizId' element={<Questions quizzes={quizzes} userId={userId} />} />
          <Route path='create/quizzes' element={<QuizPage quizzes={quizzes} onAddQuiz={handleAddQuiz} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
