import React, { useState } from 'react';
import SingleQuiz from './SingleQuiz';
import './Quizzes.css';

function Quizzes({ quizzes }) {
  console.log(quizzes)
  const [updatedQuizzes, setUpdatedQuizzes] = useState(quizzes);
  console.log(updatedQuizzes)
  const handleUpdateQuiz = (quizId, newTitle, newDescription) => {
    const updatedQuizzesArray = updatedQuizzes.map(quiz =>
      quiz.id === quizId ? { ...quiz, title: newTitle, description: newDescription } : quiz
    );
    setUpdatedQuizzes(updatedQuizzesArray);
  };

  const handleDeleteQuiz = (quizId) => {
    const updatedQuizzesArray = updatedQuizzes.filter((quiz) => quiz.id !== quizId);
    setUpdatedQuizzes(updatedQuizzesArray);
  };
  console.log(updatedQuizzes)
  const flattenedQuizzes = quizzes.flat(); // flatten the array

  return (
    <div className='quiz-list d-flex flex-wrap justify-content-center'>
      {flattenedQuizzes.map(quiz => {
        if (quiz && quiz.id) { // check if quiz is not null or undefined and has an id
          return <SingleQuiz key={quiz.id} quiz={quiz} onUpdateQuiz={handleUpdateQuiz} onDeleteQuiz={handleDeleteQuiz}/>;
        } else {
          return null; // or you can return a default component or message
        }
      })}
    </div>
  );
}

export default Quizzes;
