import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import SingleQuestion from './SingleQuestion';
import './Questions.css';
import { Link } from 'react-router-dom';

function Questions( {userId} ) {
  const params = useParams(); // Get the URL parameters object
  const quizId = params.quizId; // Access the quizId property

  const [questions, setQuestions] = useState([]);
  const [rating, setRating] = useState ('')
  const [review_text, setReview_text] = useState ('')

  useEffect(() => {
    console.log('quizId:', quizId);  // Debugging line
    if (quizId) {
      fetch(`http://127.0.0.1:5000/participant/quizzes/${quizId}/questions`, {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
          "Authorization": "Bearer " + localStorage.getItem('token')
        }
      })
        .then(response => {
          if (!response.ok) {
            console.error('Network response was not ok', response.status, response.statusText);
            throw new Error('Network response was not ok');
          }
          return response.json();
        })
        .then(data => {
          console.log('Fetched questions:', data);
          setQuestions(data);
        })
        .catch(error => console.error('Error fetching questions:', error));
    }
  }, [quizId]);
  console.log(questions)

  function handleSubmitReview (e) {
    e.preventDefault();
   fetch("http://127.0.0.1:5000/participant/reviews", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": 'Bearer ' + localStorage.getItem('token')
      },
      body: JSON.stringify({
       rating: rating,
       review_text:review_text,
       quizId: quizId
      }),
    })
      .then((r) => r.json())
      .then((newReview) => {
          setRating('');
          setReview_text('');
          console.log(newReview)
      })
      .catch((error) => {
        console.error("Error creating review:", error);
      });
  }




  return (
    <div>
      <h2 className='header'>Questions for Quiz {quizId}</h2>
      <ul>
        {questions.map(question => <SingleQuestion key ={question.id} question={question} userId={userId}/>)}
      </ul>
      <div className='reviews'>
        <p>How would you rate this quiz on a scale of one to ten ðŸ¤”</p>
        <form>

        </form>
      </div>
      <Link to = {'/quizzes'}>
      <button className='back-btn'>Back</button>

       </Link>
    </div>
  );
}

export default Questions;
