import React, { useState, useEffect } from 'react';

function UpdateQuiz({ quizzes, handleUpdateQuiz, onRefresh }) {
    console.log(quizzes)
  const [updatedQuiz, setUpdatedQuiz] = useState({ ...quiz });

  useEffect(() => {
    setUpdatedQuiz({ ...quiz });
  }, [quiz]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedQuiz({
      ...updatedQuiz,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch(`http://127.0.0.1:5000/quizzes/${updatedQuiz.id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + localStorage.getItem('token')
      },
      body: JSON.stringify(updatedQuiz)
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        handleUpdateQuiz(data);
        onRefresh();
      })
      .catch(error => console.error('Error updating quiz:', error));
  };

  return (
    <div>
      <h2>Update Quiz</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={updatedQuiz.title} onChange={handleChange} />
        </label>
        <label>
          Description:
          <textarea name="description" value={updatedQuiz.description} onChange={handleChange} />
        </label>
        <button type="submit">Update Quiz</button>
      </form>
    </div>
  );
}

export default UpdateQuiz;
