import './App.css';
import Login from './pages/Login/Login';
import useFetch from './useFetch';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Quizzes from './quizzes/Quizzes';
import Questions from './questions/Questions';
import axios from 'axios';
import { useState,useEffect } from 'react';
import QuizForm from './quizzes/QuizForm';
import QuizPage from './quizzes/QuizPage';

function App() {
  const [userId, setUserId] = useState(null);
  const [refresh, setRefresh] = useState(false)

  useEffect(() => {
    // Get user ID from backend API
    axios.get('/api/user')
      .then(response => {
        setUserId(response.data.id);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  const { data, errors } = useFetch("http://127.0.0.1:5000/participant/quizzes")

  console.log(data, errors)


  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/' element={<Login />} />
          <Route path='/quizzes' element={<Quizzes quizzes={data} userId={userId} />} />
          <Route path='quizzes/questions/:quizId' element={<Questions quizzes={data} userId={userId} />} />
          <Route path= 'create/quizzes' element = {<QuizPage quizzes = {data} />}/>
        </Routes>
      </Router>
    </div>
  );
}

export default App;
