import React, { useState, useEffect } from 'react';
import SingleQuiz from './SingleQuiz';
import './Quizzes.css'; // Import your CSS file
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

function Quizzes({ quizzes }) {
  const [updatedQuizzes, setUpdatedQuizzes] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (quizzes) {
      setUpdatedQuizzes(quizzes);
    }
  }, [quizzes]);

  const handleUpdateQuiz = (quizId, newTitle, newDescription) => {
    setUpdatedQuizzes(updatedQuizzes =>
      updatedQuizzes.map(quiz =>
        quiz.id === quizId ? { ...quiz, title: newTitle, description: newDescription } : quiz
      )
    );
  };

  const handleDeleteQuiz = async (quizId) => {
    try {
      const response = await axios.delete(`http://127.0.0.1:5000/creator/quizzes/${quizId}`, {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.status === 204) {
        setUpdatedQuizzes(updatedQuizzes => updatedQuizzes.filter(quiz => quiz.id !== quizId));
      } else {
        throw new Error('Failed to delete quiz');
      }
    } catch (error) {
      console.error(`Error deleting quiz: ${error.message}`);
      alert('Failed to delete quiz. Please try again.');
    }
  };

  const handleLogout = async () => {
    try {
      const response = await axios.delete('http://127.0.0.1:5000/logout', {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      if (response.status === 204) {
        localStorage.removeItem('token');
        navigate('/', { replace: true });
      } else {
        throw new Error('Failed to log out');
      }
    } catch (error) {
      console.error(`Error logging out: ${error.message}`);
      alert('Error logging out. Please try again.');
    }
  };

  return (
    <div>
      <div className="top-buttons">
        <Link to='create/quizzes'>
          <button className="create-button">Create Quizzes</button>
        </Link>
        <button className="logout-button" onClick={handleLogout}>Logout</button>
      </div>
      <div className="quiz-list d-flex flex-wrap justify-content-center">
        {updatedQuizzes.map(quiz => {
          if (quiz && quiz.id) {
            return (
              <div key={quiz.id} className="quiz-item">
                <h3>{quiz.title}</h3>
                <p>{quiz.description}</p>
                <div className="actions">
                  <button className="update-button" onClick={() => handleUpdateQuiz(quiz.id, 'New Title', 'New Description')}>
                    Update
                  </button>
                  <button className="delete-button" onClick={() => handleDeleteQuiz(quiz.id)}>
                    Delete
                  </button>
                </div>
              </div>
            );
          } else {
            return null;
          }
        })}
      </div>
    </div>
  );
}

export default Quizzes;
