import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './Quizzes.css';
import { Link } from 'react-router-dom';

function SingleQuiz({ quiz }) {
  return (
    <div className="card">
        <div className="card-body">
          <img src="path/to/your/image.jpg" alt="Quiz" className="card-img-top" />
          <h5 className="card-title">{quiz.title}</h5>
          <h6 className="card-subtitle mb-2 text-body-secondary">{quiz.description}</h6>
          <p className="card-text">Created At: {quiz.created_at}</p>
          <p className="card-text">Updated At: {quiz.updated_at}</p>
      <Link to={`questions/${quiz.id}`}>
          <button>Take quiz</button>
      </Link>
      <Link to = {'/questions/{'}>
      <button>
        Update Quiz
        </button></Link>
        </div>
    </div>
  );
}

export default SingleQuiz;
