import './App.css';
import Login from './pages/Login/Login';
import useFetch from './useFetch';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Quizzes from './quizzes/Quizzes';
import Questions from './questions/Questions';
import axios from 'axios';
import { useState, useEffect } from 'react';

function App() {
  const [userId, setUserId] = useState(null);
  const [quizzes, setQuizzes] = useState([]);

  useEffect(() => {
    // Get user ID from backend API
    axios.get('/api/user')
     .then(response => {
        setUserId(response.data.id);
      })
     .catch(error => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    // Fetch quizzes from backend API
    axios.get('http://127.0.0.1:5000/participant/quizzes')
     .then(response => {
        setQuizzes(response.data);
      })
     .catch(error => {
        console.error(error);
      });
  }, []);

  const handleUpdateQuiz = async (updatedQuiz) => {
    try {
      const response = await axios.patch(`http://127.0.0.1:5000/participant/quizzes/${updatedQuiz.id}`, updatedQuiz);
      const updatedQuizzes = quizzes.map(quiz => quiz.id === updatedQuiz.id? updatedQuiz : quiz);
      setQuizzes(updatedQuizzes);
      console.log(`Quiz updated successfully: ${response.data}`);
    } catch (error) {
      console.error(`Error updating quiz: ${error}`);
    }
  };

  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/' element={<Login />} />
          <Route path='/quizzes' element={<Quizzes quizzes={quizzes} userId={userId} handleUpdateQuiz={handleUpdateQuiz} />} />
          <Route path='quizzes/questions/:quizId' element={<Questions quizzes={quizzes} userId={userId} />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
