import React from 'react';
import Response from '../responses/Response';
import ResponseForm from '../responses/ResponseForm';
import './SingleQuestion.css';

function SingleQuestion({ question, userId }) {
  console.log(question);

  return (
    <div className="single-question-container">
      <h3 className="question-text">{question.question_text}</h3>
      <ul className="choices-list">
        <li className="choice">A.  {question.choice_1}</li>
        <li className="choice">B.  {question.choice_2}</li>
        <li className="choice">C.  {question.choice_3}</li>
        <li className="choice">D.  {question.choice_4}</li>
      </ul>

      <ResponseForm 
        question_id={question.id} 
        quiz_id={question.quiz_id} 
        user_id={userId} 
      />
    </div>
  );
}

export default SingleQuestion;
