import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './SingleQuiz.css';
import { Link } from 'react-router-dom';

function SingleQuiz({ quiz }) {
  return (
    <div className='container'>
      <div className='row justify-content-center'>
        <div className='col-md-6'>
          <div className="card">
            <div className="card-body">
              <img src="path/to/your/image.jpg" alt="Quiz" className="card-img-top" />
              <h5 className="card-title">{quiz.title}</h5>
              <h6 className="card-subtitle mb-2 text-muted">{quiz.description}</h6>
              <p className="card-text">Created At: {quiz.created_at}</p>
              <p className="card-text">Updated At: {quiz.updated_at}</p>
              <Link to={`questions/${quiz.id}`}>
                <button className='btn btn-primary'>Take quiz</button>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default SingleQuiz;
