import React, { useState } from 'react';
import Response from './Response';

const ResponseForm = ({ question_id, quiz_id, user_id }) => {
  const [response, setResponse] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [data1, setData1]= useState('')

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Response submitted:', response);

    // Make a POST request to create a new response
    fetch('http://localhost:5000/participant/responses', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        "Authorization": "Bearer " + localStorage.getItem('token')
      },
      body: JSON.stringify({ 
        response: response, 
        question_id: question_id, 
        quiz_id: quiz_id, 
        user_id: user_id 
      })
    })
      .then(response => response.json())
      .then(data => {
        console.log('Response created:', data);
        setIsSubmitted(true);
        setData1(data);

        if (data.is_correct) {
          alert('Correct answer!');
        } else {
          alert('Incorrect answer. Try again!');
        }
      })
      .catch(error => console.error('Error creating response:', error));
  };
}
export default ResponseForm;
