import React from 'react'

function QuizPage({quizzes}) {
    console.log(quizzes)
    const flattenedQuizzes = quizzes.flat(); // flatten the array

    return (
      <div>
        {flattenedQuizzes.map(quiz => {
          if (quiz && quiz.id) { // check if quiz is not null or undefined and has an id
            return <SingleQuiz key={quiz.id} quiz={quiz} handleUpdateQuiz={handleUpdateQuiz}/>;
          } else {
            return null; // or you can return a default component or message
          }
        })}
      </div>
    );
}

export default QuizPage
