import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './Quizzes.css';
import { Link } from 'react-router-dom';

function SingleQuiz({ quiz, handleUpdateQuiz, handleDeleteQuiz }) {
  const [title, setTitle] = useState(quiz.title);
  const [description, setDescription] = useState(quiz.description);
  const [isEditing, setIsEditing] = useState(false);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    const updatedQuiz = { ...quiz, title, description };
    handleUpdateQuiz(updatedQuiz);
    setIsEditing(false);
  };

  const handleDeleteClick = () => {
    handleDeleteQuiz(quiz.id);
  };

  return (
    <div className="card">
      <div className="card-body">
        <img src="path/to/your/image.jpg" alt="Quiz" className="card-img-top" />
        {isEditing ? (
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="form-control"
          />
        ) : (
          <h5 className="card-title">{title}</h5>
        )}
        {isEditing ? (
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="form-control"
          />
        ) : (
          <h6 className="card-subtitle mb-2 text-body-secondary">{description}</h6>
        )}
        <p className="card-text">Created At: {quiz.created_at}</p>
        <p className="card-text">Updated At: {quiz.updated_at}</p>
        {isEditing ? (
          <button onClick={handleSaveClick}>Save</button>
        ) : (
          <button onClick={handleEditClick}>Edit</button>
        )}
        <button onClick={handleDeleteClick}>Delete</button>
        <Link to={`questions/${quiz.id}`}>
          <button>Take quiz</button>
        </Link>
      </div>
    </div>
  );
}

export default SingleQuiz;
