import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './SingleQuiz.css';
import { Link } from 'react-router-dom';

function SingleQuiz({ quiz }) {
  return (
    <div className="container">
      <div className="d-flex flex-wrap">

          <div key={quiz.id} className="card m-2">
            <img src={`https://example.com/images/${quiz.image}`} alt="Quiz" className="card-img-top" style={{ maxHeight: 200 }} />
            <div className="card-body">
              <h5 className="card-title">{quiz.title}</h5>
              <h6 className="card-subtitle mb-2 text-body-secondary">{quiz.description}</h6>
              <p className="card-text">Created At: {quiz.created_at}</p>
              <p className="card-text">Updated At: {quiz.updated_at}</p>
              <Link to={`questions/${quiz.id}`}>
                <button className="card-btn">Take quiz</button>
              </Link>
            </div>
          </div>

      </div>
    </div>
  );
}

export default SingleQuiz;
