import React from 'react';
import ResponseForm from '../responses/ResponseForm';
import './SingleQuestion.css'; // Import your CSS file

function SingleQuestion({ question, userId }) {
  console.log(question);
  return (
    <div className="single-question-container">
      <div className="question-text">
        <h3>{question.question_text}</h3>
      </div>
      <ul className="choices-list">
        <li className="choice">{question.choice_1}</li>
        <li className="choice">{question.choice_2}</li>
        <li className="choice">{question.choice_3}</li>
        <li className="choice">{question.choice_4}</li>
      </ul>

      <ResponseForm
        question_id={question.id}
        quiz_id={question.quiz_id}
        user_id={userId}
        className="response-form"
      />
    </div>
  );
}

export default SingleQuestion;
