import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './SingleQuiz.css';
import { Link } from 'react-router-dom';

function SingleQuiz({ quiz, onUpdateQuiz, onDeleteQuiz }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(quiz.title);
  const [description, setDescription] = useState(quiz.description);

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleUpdateQuiz = () => {
    fetch(`http://127.0.0.1:5000/creator/quizzes/${quiz.id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '+ localStorage.getItem('token')
      },
      body: JSON.stringify({
        title,
        description
      })
    })
   .then(response => response.json())
   .then(data => {
      onUpdateQuiz(quiz.id, title, description);
      setIsEditing(false);
    })
   .catch(error => {
      console.error(error);
    });
  };

  const handleCancelEdit = () => {
    setTitle(quiz.title);
    setDescription(quiz.description);
    setIsEditing(false);
  };

  const handleDeleteQuiz = () => {
    if (window.confirm(`Are you sure you want to delete quiz "${quiz.title}"?`)) {
      fetch(`http://127.0.0.1:5000/creator/quizzes/${quiz.id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': 'Bearer '+ localStorage.getItem('token')
        }
      })
     .then(response => response.json())
     .then(data => {
        onDeleteQuiz(quiz.id);
      })
     .catch(error => {
        console.error(error);
      });
    }
  };

  return (
    <div className='card-list'>
      <div className="card">
        <div className="card-body">
          <img src="path/to/your/image.jpg" alt="Quiz" className="card-img-top" />
          {isEditing? (
            <input
              type="text"
              value={title}
              onChange={handleTitleChange}
              className="form-control"
            />
          ) : (
            <h5 className="card-title">{title}</h5>
          )}
          {isEditing? (
            <textarea
              value={description}
              onChange={handleDescriptionChange}
              className="form-control"
            />
          ) : (
            <h6 className="card-subtitle mb-2 text-body-secondary">{description}</h6>
          )}
          <p className="card-text">Created At: {quiz.created_at}</p>
          <p className="card-text">Updated At: {quiz.updated_at}</p>
          {isEditing? (
            <div>
              <button className='card-btn' onClick={handleUpdateQuiz}>Save</button>
              <button className='card-btn' onClick={handleCancelEdit}>Cancel</button>
            </div>
          ) : (
            <div>
              <button className='update-button' onClick={() => setIsEditing(true)}>Edit</button>
              <button className='delete-button' onClick={handleDeleteQuiz}>Delete</button>
              <Link to={`questions/${quiz.id}`}>
                <button className='take-quiz-button'>Take quiz</button>
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default SingleQuiz;
