import React, { useState, useEffect } from 'eact';
import SingleQuiz from './SingleQuiz';

const QuizList = ({ quizzes }) => {
  const [updatedQuizzes, setUpdatedQuizzes] = useState(quizzes);

  useEffect(() => {
    setUpdatedQuizzes(quizzes);
  }, [quizzes]);

  const handleUpdateQuiz = (updatedQuiz) => {
    const updatedQuizzesArray = updatedQuizzes.map((quiz) => {
      if (quiz.id === updatedQuiz.id) {
        return updatedQuiz;
      }
      return quiz;
    });
    setUpdatedQuizzes(updatedQuizzesArray);
  };

  const handleDeleteQuiz = (quizId) => {
    const updatedQuizzesArray = updatedQuizzes.filter((quiz) => quiz.id!== quizId);
    setUpdatedQuizzes(updatedQuizzesArray);
  };

  return (
    <div className='quiz-list d-flex flex-wrap justify-content-center'>
      {updatedQuizzes.map((quiz) => {
        if (quiz && quiz.id) {
          return (
            <SingleQuiz
              key={quiz.id}
              quiz={quiz}
              onUpdateQuiz={handleUpdateQuiz}
              onDeleteQuiz={handleDeleteQuiz}
            />
          );
        } else {
          return null;
        }
      })}
    </div>
  );
};

export default QuizList;
