import React from 'react';
import SingleQuestion from './SingleQuestion';

function Questions({ questions }) {
  console.log({ questions});
  const flattenedQuestions = questions.flat(); // flatten the array

  return (
    <div>
      {flattenedQuestions.map(question => {
        if (question && question.id) { // check if quiz is not null or undefined and has an id
          return <SingleQuestion key={question.id} question={question} />;
        } else {
          return null; // or you can return a default component or message
        }
      })}
    </div>
  );
}

export default Questions;
