import React from 'react';
import SingleQuiz from './SingleQuiz';

function Quizzes({ quizzes }) {
  console.log({ quizzes });
  const flattenedQuizzes = quizzes.flat(); // flatten the array

  return (
    <div>
      {flattenedQuizzes.map(quiz => (
        quiz ? <SingleQuiz key={quiz.id} quiz={quiz} /> : null
      ))}
    </div>
  );
}

export default Quizzes;
