import React, { useState } from 'react';
import './Login.css';
import { useNavigate } from 'react-router-dom';

const Login = ({ setEmail }) => {
  const navigate = useNavigate()

  const [userName, setUserName] = useState("");
  const [email, setEmailLocal] = useState("");
  const [password, setPassword] = useState("");
  const [isActive, setIsActive] = useState(false);

  function handleSubmitLogin(e){
    e.preventDefault();
    fetch('http://127.0.0.1:5000/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email: email, password: password })
    })
    .then (res => res.json())
    .then (data => {
      console.log(data.token)
      localStorage.setItem("token", data.token)
      localStorage.setItem("refresh_token", data.refresh_token)

      if (data.token){
        navigate('/quizzes')
      }

    })

    }


    function handleSubmitSignup(e) {
      e.preventDefault();

      const userName = document.getElementById('username').value;
      const email = document.getElementById('email').value;
      const password = document.getElementById('password').value;

      fetch('http://127.0.0.1:5000/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: userName, email: email, password: password })
      })
      .then(res => {
        if (!res.ok) {
          return res.json().then(err => { throw new Error(err.message); });
        }
        return res.json();
      })
      .then((data) => {
        console.log("User registered successfully", data);
        alert("User registered successfully: " + data.message); 
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Error: ' + error.message); 
      });
    }




  return (
    <div className={`container ${isActive ? 'active' : ''}`} id="container">
      <div className="form-container sign-up">
        <form onSubmit={handleSubmitSignup}>
          <h1>Create Account</h1>
          <span>Register with Email</span>
          <input type="text" placeholder="Username" value={userName} onChange={(e) => setUserName(e.target.value)} />
          <input type="text" placeholder="Email" value={email} onChange={(e) => setEmailLocal(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button>Sign Up</button>
        </form>

      </div>

      <div className="form-container sign-in">
        <form onSubmit={handleSubmitLogin}>
          <h1>Sign In</h1>
          <span>Sign in with Email</span>
          <input type="text" placeholder="Email" value={email} onChange={(e) => setEmailLocal(e.target.value)} />
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button>Sign In</button>
          {/* <a href="#" className="forgot-password">Forgot Password?</a> */}
        </form>
      </div>

      <div className="toggle-container">
            <div className="toggle">
                <div className="toggle-panel toggle-left">
                    <h1>Quizzy🌶️</h1>
                    <p>The World's Best Quiz App. Ever.</p>
                    <button className="hidden" id="login" onClick={() => {setIsActive(false)}}>Sign In</button>
                </div>
                <div className="toggle-panel toggle-right">
                    <h1>Quizzy🫶🏾</h1>
                    <p>The World's Best Quiz App. Ever.</p>
                    <button className="hidden" id="register" onClick={() =>{setIsActive(true)}}>Sign Up</button>
                </div>
            </div>
        </div>

    </div>
  );
};

export default Login;
