import React from 'react'
import Response from '../responses/Response'
import ResponseForm from '../responses/ResponseForm'

function SingleQuestion({ question, userId }) {
  console.log(question)
  return (
    <div>
      <div>
      <ul>
        <li className='single-question'>
        {question.question_text}<br></br>
        </li>
      </ul>
      </div>
      {/* {question.question_text}<br></br> */}
      {question.choice_1}<br></br>
      {question.choice_2}<br></br>
      {question.choice_3}<br></br>
      {question.choice_4}

      <ResponseForm 
        question_id={question.id} 
        quiz_id={question.quiz_id} 
        user_id={userId} 
      />
    </div>
  )
}

export default SingleQuestion
